const navLinks=document.querySelector(".nav__links"),arrOfNavLinks=document.querySelectorAll(".nav__link"),navButton=document.querySelector(".top-bar__button"),header=document.querySelector(".header"),body=document.querySelector(".page__body");function pageNavigation(){navButton.classList.toggle("top-bar__button--close"),navLinks.classList.toggle("nav__links--open"),header.classList.toggle("header--menu"),body.classList.toggle("page__body--fixed")}function isLargeScreen(){return window.matchMedia("(max-width: 1023px)").matches}navButton.addEventListener("click",pageNavigation),arrOfNavLinks.forEach((e=>{e.addEventListener("click",(()=>{isLargeScreen()&&pageNavigation()}))})),window.addEventListener("scroll",(()=>{window.scrollY>80?navLinks.classList.add("nav__links--dark-green"):navLinks.classList.remove("nav__links--dark-green")}));